con:config.script:
import jxl.* //import Java Excel library
def TestCase = context.testCase def path = context.expand ( '${projectDir}') def count
Workbook WorkBook1 = Workbook.getWorkbook(new File(path + "/Words.xls")) Sheet Sheet1 = WorkBook1.getSheet(0) //PropertiesTestStep = TestCase.getTestStepByName("Properties") PropertiesTestCase = testRunner.testCase count = PropertiesTestCase.getPropertyValue("Counter").toInteger()
//If total records unknown (at start), get the rowcount from Excel if (PropertiesTestCase.getPropertyValue("Total").toString() == "") PropertiesTestCase.setPropertyValue("Total", Sheet1.getRows().toString())
count++
//Read the Excel test data Cell Field1 = Sheet1.getCell(0, count) Cell Field2 = Sheet1.getCell(1, count) log.info("Count is " + count.toString() + " badWord : " + Field1.getContents() + " rightWord : " + Field2.getContents()) WorkBook1.close()
//Copy the Excel test data to properties in Properties test case PropertiesTestCase.setPropertyValue("badWord", Field1.getContents() ) PropertiesTestCase.setPropertyValue("rightWord", Field2.getContents() ) PropertiesTestCase.setPropertyValue("Counter", count.toString() )
if (count == PropertiesTestCase.getPropertyValue("Total").toInteger() - 1) PropertiesTestCase.setPropertyValue("End", "True")

//def TestCase = context.testCase

//PropertiesTestStep = TestCase.getTestStepByName("Properties")
PropertiesTestCase = testRunner.testCase
//Stop = PropertiesTestStep.getPropertyValue("End").toString()
Stop = PropertiesTestCase.getPropertyValue("End").toString()
if (Stop == "True")
	log.info("Exit Groovy Script - DataLoop")
else
	testRunner.gotoStepByName("Groovy Script")
