con:config.script:
import jxl.* //import Java Excel library
def TestCase = context.testCase def FilePath = "C:\\Документы\\Words.xls" def count
Workbook WorkBook1 = Workbook.getWorkbook(new File(FilePath)) Sheet Sheet1 = WorkBook1.getSheet(0) PropertiesTestStep = TestCase.getTestStepByName("Properties") count = PropertiesTestStep.getPropertyValue("Counter").toInteger()
//If total records unknown (at start), get the rowcount from Excel if (PropertiesTestStep.getPropertyValue("Total").toString() == "") PropertiesTestStep.setPropertyValue("Total", Sheet1.getRows().toString())
count++
//Read the Excel test data Cell Field1 = Sheet1.getCell(0, count) Cell Field2 = Sheet1.getCell(1, count) log.info("Count is " + count.toString() + " badWord : " + Field1.getContents() + " rightWord : " + Field2.getContents()) WorkBook1.close()
//Copy the Excel test data to properties in Properties test step PropertiesTestStep.setPropertyValue("badWord", Field1.getContents() ) PropertiesTestStep.setPropertyValue("rightWord", Field2.getContents() ) PropertiesTestStep.setPropertyValue("Counter", count.toString() )
if (count == PropertiesTestStep.getPropertyValue("Total").toInteger() - 1) PropertiesTestStep.setPropertyValue("End", "True")
